	(168, 14, 3, 'example', 4, '<p> \'mode\' =&gt; \'popup\', //mode: \'popup\' or \'inline\'</p><pre>\'defaults\' =&gt; array( //default settings for all editable elements<br>\'emptytext\' =&gt; \'Натисніть для редагування\'<br>)<br> ),<br>),<br><br>// application-level parameters that can be accessed<br>// using Yii::app()-&gt;params[\'paramName\']<br>\'params\'=&gt;array(<br> // this is used in teacher profile page<br>\'adminEmail\'=&gt;\'Wizlightdragon@gmail.com\',<br>\'languages\'=&gt;array(\'en\'=&gt;\'English\', \'ua\'=&gt;\'Ukrainian\', \'ru\'=&gt;\'Russian\'),<br>//\'imagesPath\'=&gt;\'http://localhost/IntITA/images\',<br>\'imagesPath\'=&gt;\'http://intita.itatests.com/images\',<br>\'avatarsPath\'=&gt;\'http://intita.itatests.com/avatars\',<br>\'commonPath\' </pre>'),
